htmlform.script:
$j(document).ready(function() {
	getField('HCG.value').change(function() {
		var BetaHCG = getValue("HCG.value");
		if( BetaHCG != null && BetaHCG >= 500 && BetaHCG < 10000 ) {
			var BHCGmL = calculateBetaHCG(BetaHCG);
				setValue('HCG.value', BHCGmL);
				}

		});
	});

	function calculateBetaHCG(BetaHCG) {
	    var BHCG;

	    BHCG = (BetaHCG*1000)

	    return BHCG;
	}
