    		  if($j('#breastCancerStagingKeys'+i).is(':hidden')){
    			  $j('#breastCancerStagingKeys'+i).show();
    			  $j('#breastCancerStagingKeys'+i).clone().appendTo('.selectedCancerStagingKeys');  
    		  }else{
    		      $j('#breastCancerStagingKeys'+i).clone().appendTo('.selectedCancerStagingKeys');  
    		  }
    	  }    	  
       };

