if (jQuery) {
    $j(document).ready(function () {

        $j(function () {
            $j("#obs1").hide();
            $j("#obs2").hide();
            var myvalue1 = $j("#look1").text().trim();
            var myvalue2 = $j("#look2").text().trim();
            setValue('obs_value1.value', myvalue1);
            setValue('obs_value2.value', formatDate(myvalue2));
        });


        function formatDate(mydate) {
            var d = mydate.split("/");
            var month = d[1];
            var day = d[0];
            var year = d[2];

            switch (month) {
                case 'Jan':
                    month = "01";
                    break;
                case 'Feb':
                    month = "02";
                    break;
                case 'Mar':
                    month = "03";
                    break;
                case 'Apr':
                    month = "04";
                    break;
                case 'May':
                    month = "05";
                    break;
                case 'Jun':
                    month = "06";
                    break;
                case 'Jul':
                    month = "07";
                    break;
                case 'Aug':
                    month = "08";
                    break;
                case 'Sept':
                    month = "09";
                    break;
                case 'Oct':
                    month = "10";
                    break;
                case 'Nov':
                    month = "11";
                    break;
                case 'Dec':
                    month = "12";
                    break;
                default:
                    console.log(month);
            }

            return [year, month, day].join('-');
        }

        var url = new URL(window.location.href);

        if (url.searchParams.get("mode") == "EDIT") {//Edit
            console.log("Mode Edit");
            $j('.other_reason_here').hide();
            hideByDefault();
        } else if (url.searchParams.get("formId")) {//Creation
            $j('.other_reason_here').hide();
            console.log("Mode formId");
        } else {//View the form
            console.log("Mode view");
            $j('.other_reason_here').hide();
            hideByDefault();
        }

        function hideByDefault() {
            toggleInput('artChange');
            toggleInput('toxicity');
            toggleInput('otherARVChange');
            toggleInput('death');
            toggleInput('attended_enhanced');
            toggleInput('tptInPregress');
            toggleInput('riskFactorYes');
            toggleInput('employmentStatus');
        }


        $j('#artChange').click(function () {
            toggleInput('artChange');
        });

        $j('#toxicity').change(function () {
            toggleInput('toxicity');
        });

        $j('#otherARVChange').change(function () {
            toggleInput('otherARVChange');
        });

        $j('#death').change(function () {
            toggleInput('death');
        });

        $j('#attended_enhanced').click(function () {
            toggleInput('attended_enhanced');
        });

        $j('#tptInPregress').change(function () {
            toggleInput('tptInPregress');
        });

        $j('#riskFactorYes').click(function () {
            toggleInput('riskFactorYes');
        });

        $j('#demoChange').click(function () {
            toggleInput('demoChange');
        });

        $j('#employmentStatus').click(function () {
            toggleInput('employmentStatus');
        });


        function toggleInput(mode) {
            switch (mode) {
                case 'artChange':
                    if (!getValue('artChange.value')) {
                        $j('#reasonArtChange').hide();
                        $j('#reasonArtChange_continue').hide();
                        return;
                    }
                    if (getValue('artChange.value') == "1065") {
                        $j('#reasonArtChange').show();
                        $j('#reasonArtChange_continue').show();
                    } else {
                        $j('#reasonArtChange').hide();
                        $j('#reasonArtChange_continue').hide();
                    }
                    break;
                case 'toxicity':
                    if (!getValue('toxicity.value')) {
                        $j('#toxicityTypes').hide();
                        return;
                    }
                    if (getValue('toxicity.value') == "102") {
                        $j('#toxicityTypes').show();
                    } else {
                        $j('#toxicityTypes').hide();
                    }
                    break;
                case 'otherARVChange':
                    if (!getValue('otherARVChange.value')) {
                        $j('#otherARVReason').hide();
                        return;
                    }
                    if (getValue('otherARVChange.value') == "5622") {
                        $j('#otherARVReason').show();
                    } else {
                        $j('#otherARVReason').hide();
                    }
                    break;
                case 'death':
                    if (!getValue('death.value')) {
                        $j('#deathCause').hide();
                        $j('#transferto').hide();
                        return;
                    }
                    if (getValue('death.value') == "12710") {
                        $j('#deathCause').show();
                        $j('#transferto').hide();
                    } else if (getValue('death.value') == "3003") {
                        $j('#deathCause').hide();
                        $j('#transferto').show();
                    } else {
                        $j('#deathCause').hide();
                        $j('#transferto').hide();
                    }
                    break;
                case 'attended_enhanced':
                    if (!getValue('attended_enhanced.value')) {
                        $j('#attended_enhanced_answer').hide();
                        return;
                    }
                    if (getValue('attended_enhanced.value') == "1065") {
                        $j('#attended_enhanced_answer').show();
                    } else {
                        $j('#attended_enhanced_answer').hide();
                    }
                    break;
                case 'tptInPregress':
                    if (!getValue('tptInPregress.value')) {
                        $j('#tptProgressReasons').hide();
                        return;
                    }
                    if (getValue('tptInPregress.value') == "105600") {
                        $j('#tptProgressReasons').show();
                    } else {
                        $j('#tptProgressReasons').hide();
                    }
                    break;
                case 'riskFactorYes':
                    if (!getValue('riskFactorYes.value')) {
                        $j('#sexualPartners').hide();
                        return;
                    }
                    if (getValue('riskFactorYes.value') == "1065") {
                        $j('#sexualPartners').show();
                    } else {
                        $j('#sexualPartners').hide();
                    }
                    break;
                case 'employmentStatus':
                    console.log(getValue('employmentStatus.value'));
                    if (!getValue('employmentStatus.value')) {
                        $j('#occupation').hide();
                        return;
                    }
                    if (getValue('employmentStatus.value') == "1065") {
                        $j('#occupation').show();
                    } else {
                        $j('#occupation').hide();
                    }
                    break;
                case 'demoChange':
                    if (getValue('demoChange.value') == "1065") {
                        alert(" Please update the demographic information in the Demographic patient Form");
                    } else {
                        console.log("no change");
                    }
                    break;
                default:
                    console.log(mode);
            }
        }


        //If  submission does not work redirect with an error message
        beforeSubmit.push(function () {

            var date = getValue('encounterDate.value');
            var provider = getValue('encounterProvider.value');
            var location = getValue('encounterLocation.value');
            var demographicChange = getValue('demoChange.value');

            if (!date) {
                getField('encounterDate.error').html('This field is required').show();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                return false;
            } else {
                getField('encounterDate.error').html('This field is required').hide();
            }
            if (!provider) {
                getField('encounterProvider.error').html('This field is required').show();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                return false;
            } else {
                getField('encounterProvider.error').html('This field is required').hide();
            }
            if (!location) {
                getField('encounterLocation.error').html('This field is required').show();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                return false;
            } else {
                getField('encounterLocation.error').html('This field is required').hide();
            }
            if (!demographicChange) {
                getField('demoChange.error').html('This field is required').show();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                return false;
            } else {
                getField('demoChange.error').html('This field is required').hide();
            }
            return true;
        });

    });
}
