htmlform.script:
$j(document).ready(function() {

	$j('.heightForAge').hide();
	$j('.weightForAge').hide();
	$j('.heightWeight').hide();
	$j('.lastHeight').hide();

	var lastHeight = "";
	var lastHeightDate = "";

	var birthDate = $j('.birthdate').text();
	var date = getValue('encounter.value');

	var months = monthDiff(birthDate, date);

	if(months > 228)
	{
		$j('.lastHeight').show();
	}

	getField('height.value').change(function() {
		var patientId = $j('[name=personId]').val();
		var gender = $j('.gender').text();

		var length = getValue("height.value");

		if(date != null)
		{ 

			if(months < 229)
			{
				var zscore = calculateZScoreLength(gender, months, length);

				setValue('heightForAge.value', zscore);
				$j('.zscoreHeight').html(zscore);
				$j('.heightForAge').show();
			}

			if(months < 25)
			{
				var weight = getValue("weight.value");
				if(length != null && length > 45 && length < 110)
				{
					var percentage = calculateLengthWeightPercent(gender, length, weight);

					setValue('heightWeightPercent.value', percentage);
					$j('.heightWeightPercentage').html(percentage);
					$j('.heightWeight').show();
				}
			}
			if(months > 24 && months < 61)
			{
				var weight = getValue("weight.value");
				if(length != null && length > 65 && length < 120)
				{
					var percentage = calculateHeightWeightPercent(gender, length, weight);

					setValue('heightWeightPercent.value', percentage);
					$j('.heightWeightPercentage').html(percentage);
					$j('.heightWeight').show();
				}
			}
			if(months > 60 && months < 229)
			{
				var weight = getValue("weight.value");
				if(length != null && length > 0 && weight != null && weight > 0)
				{
					var bmiAge = calculateZScoreBMI(gender, months, weight, length);

					setValue('bmiForAgeZ.value',bmiAge);
					$j('.bmiForAgeZScore').html(bmiAge);
					$j('.bmiForAge').show();
				}
			}
			if(months > 228)
			{
				var weight = getValue("weight.value");
				if(length != null && length > 0 && weight != null && weight > 0)
				{
					var bmi = calculateBMI(length, weight);

					setValue('bmiVal.value', bmi);
					$j('.bmiValue').html(bmi);
					$j('.bmi').show();
				}
				else if (lastHeight != null && lastHeight > 0 && weight != null && weight > 0)
				{
					var monthsLastHeight = monthDiff(lastHeightDate, birthDate);
					if(monthsLastHeight > 228)
					{
						var bmi = calculateBMI(lastHeight, weight);

						setValue('bmiVal.value', bmi);
						$j('.bmiValue').html(bmi);
						$j('.bmi').show();	
					}
				}
			}
		}
	});

	getField('weight.value').change(function() {
		var patientId = $j('[name=personId]').val();

		var gender = $j('.gender').text();
		var weight = getValue("weight.value");

		if(date != null)
		{ 
			if(months < 121)
			{
				var zscore = calculateZScoreWeight(gender, months, weight);

				setValue('weightForAge.value', zscore);
				$j('.zscoreWeight').html(zscore);
				$j('.weightForAge').show();
			}

			if(months < 25)
			{
				var length = getValue("height.value");
				if(length != null && length > 45 && length < 110)
				{
					var percentage = calculateLengthWeightPercent(gender, length, weight);

					setValue('heightWeightPercent.value', percentage);
					$j('.heightWeightPercentage').html(percentage);
					$j('.heightWeight').show();
				}
			}
			if(months > 24 && months < 61)
			{
				var length = getValue("height.value");
				if(length != null && length > 65 && length < 120)
				{
					var percentage = calculateHeightWeightPercent(gender, length, weight);

					setValue('heightWeightPercent.value', percentage);
					$j('.heightWeightPercentage').html(percentage);
					$j('.heightWeight').show();
				}
			}
			if(months > 60 && months < 229)
			{
				var length = getValue("height.value");
				if(length != null && weight != null && length > 0 && weight > 0)
				{
					var bmiAge = calculateZScoreBMI(gender, months, weight, length);

					setValue('bmiForAgeZ.value', bmiAge);
					$j('.bmiForAgeZScore').html(bmiAge);
					$j('.bmiForAge').show();
				}
			}
			if(months > 228)
			{
				var length = getValue("height.value");
				if(length != null && weight != null && length > 0 && weight > 0)
				{
					var bmi = calculateBMI(length, weight);

					setValue('bmiVal.value', bmi);
					$j('.bmiValue').html(bmi);
					$j('.bmi').show();
				}
				else if (weight != null && lastHeight != null && length > 0 && weight > 0)
				{
					var monthsLastHeight = monthDiff(lastHeightDate, birthDate);
					if(monthsLastHeight > 228)
					{
						var bmi = calculateBMI(lastHeight, weight);

						setValue('bmiVal.value', bmi);
						$j('.bmiValue').html(bmi);
						$j('.bmi').show();	
					}
				}
			}
		}

		getField('encounter.value').change(function() {

			var patientId = $j('[name=personId]').val();

			var gender = $j('.gender').text();

			date = getValue("encounter.value");
			var length = getValue("height.value");
			var weight = getValue("weight.value");


			months = monthDiff(birthDate, date);
			if(months < 229)
			{
				$j('.lastHeight').hide();

				if(length != null && length > 0)
				{ 
					var zscore = calculateZScoreLength(gender, months, length);
					setValue('heightForAge.value', zscore);
					$j('.zscoreHeight').html(zscore);
					$j('.heightForAge').show();
				}
			}


			if(weight != null && weight > 0)
			{ 
				if(months < 121)
				{
					var zscore = calculateZScoreWeight(gender, months, weight);

					setValue('weightForAge.value', zscore);
					$j('.zscoreWeight').html(zscore);
					$j('.weightForAge').show();
				}

				if(months > 228)
				{
					var length = getValue("height.value");
					if(length != null)
					{
						var bmi = calculateBMI(length, weight);

						setValue('bmiVal.value', bmi);
						$j('.bmiValue').html(bmi);
						$j('.bmi').show();
					}
					else if (lastHeight != null)
					{
						var monthsLastHeight = monthDiff(lastHeightDate, birthDate);
						if(monthsLastHeight > 228)
						{
							var bmi = calculateBMI(lastHeight, weight);

							setValue('bmiVal.value', bmi);
							$j('.bmiValue').html(bmi);
							$j('.bmi').show();	
						}
					}
				}
			}

			if(weight != null && length != null)
			{
				if(months < 25)
				{
					if(length != null && length > 45 && length < 110)
					{
						var percentage = calculateLengthWeightPercent(gender, length, weight);

						setValue('heightWeightPercent.value', percentage);
						$j('.heightWeightPercentage').html(percentage);
						$j('.heightWeight').show();
					}
				}
				if(months > 24 && months < 61)
				{
					if(length != null && length > 65 && length < 120)
					{
						var percentage = calculateHeightWeightPercent(gender, length, weight);

						setValue('heightWeightPercent.value', percentage);
						$j('.heightWeightPercentage').html(percentage);
						$j('.heightWeight').show();
					}
				}
				if(months > 60 && months < 229)
				{
					if(length != null && weight != null && length > 0 && weight > 0)
					{
						var bmiAge = calculateZScoreBMI(gender, months, weight, length);

						setValue('bmiForAgeZ.value', bmiAge);
						$j('.bmiForAgeZScore').html(bmiAge);
						$j('.bmiForAge').show();
					}
				}
			}
		});
	});



	function monthDiff(date1, date2) {
	    var months;

	    var d1 = new Date();
	    d1.setTime(Date.parse(date1));

	    var d2 = new Date();
	    d2.setTime(Date.parse(date2));

	    months = (d2.getFullYear() - d1.getFullYear()) * 12;
	    if(d2.getMonth() > d1.getMonth())
	    {
	    	months = months + (d2.getMonth() - d1.getMonth());
	    }
	    else if(d1.getMonth() > d2.getMonth())
	    {
	    	months = months - (d1.getMonth() - d2.getMonth());
	    }
	   	if(d1.getDate() > d2.getDate())
	   	{
	   		months = months - 1;	
	   	}

	    return months;
	}

	function calculateZScoreLength(gender, months, length) {

		var zscore = 0;

		if(gender == "F")
		{
			zscore = (length - girlLengthMean[months])/girlLengthSD[months]; 
			zscore = zscore.toFixed(2);
		}
		else if(gender == "M")
		{
			zscore = (length - boyLengthMean[months])/boyLengthSD[months]; 
			zscore = zscore.toFixed(2);
		}

		return zscore;
	}

	function calculateLengthWeightPercent(gender, length, weight) {

		var percent = 0;

		var lengthIndex = length*10 - 450;
		if(gender == "F")
		{
			percent = weight/girlWeightLIdeal[lengthIndex] * 100; 
			percent = percent.toFixed(2);
		}
		else if(gender == "M")
		{
			percent = weight/boyWeightLIdeal[lengthIndex] * 100; 
			percent = percent.toFixed(2);
		}

		return percent;
	}

	function calculateHeightWeightPercent(gender, length, weight) {

		var percent = 0;

		var lengthIndex = length*10 - 650;
		if(gender == "F")
		{
			percent = weight/girlWeightHIdeal[lengthIndex] * 100; 
			percent = percent.toFixed(2);
		}
		else if(gender == "M")
		{
			percent = weight/boyWeightHIdeal[lengthIndex] * 100; 
			percent = percent.toFixed(2);
		}

		return percent;
	}

	function calculateZScoreWeight(gender, months, weight) {

		var zscore = 0;

		if(gender == "F")
		{
			zscore = weight/girlWeightMean[months];
			zscore = Math.pow(zscore, girlWeightL[months]);
			zscore = zscore - 1;
			zscore = zscore /(girlWeightS[months]*girlWeightL[months]);
			zscore = zscore.toFixed(2);
		}
		else if(gender == "M")
		{
			zscore = weight/boyWeightMean[months];
			zscore = Math.pow(zscore, boyWeightL[months]);
			zscore = zscore - 1;
			zscore = zscore /(boyWeightS[months]*boyWeightL[months]); 
			zscore = zscore.toFixed(2);
		}

		return zscore;
	}

	function calculateZScoreBMI(gender, months, weight, height) {

		var zscore = 0;
		months = months - 61;
		var bmi = weight/(height/100 * height/100);

		if(gender == "F")
		{
			zscore = bmi/girlBMIMean[months];
			zscore = Math.pow(zscore, girlBMIL[months]);
			zscore = zscore - 1;
			zscore = zscore /(girlBMIS[months]*girlBMIL[months]);
			zscore = zscore.toFixed(2);
		}
		else if(gender == "M")
		{
			zscore = bmi/boyBMIMean[months];
			zscore = Math.pow(zscore, boyBMIL[months]);
			zscore = zscore - 1;
			zscore = zscore /(boyBMIS[months]*boyBMIL[months]); 
			zscore = zscore.toFixed(2);
		}

		return zscore;
	}

