 function enableFn(){
   group.children("#disabled").fadeTo(250,1);
   group.children("#disabled").find("input").attr("disabled",false);
}
disableFn();
$j(this).children("#trigger").find(":checkbox:first").change(function(){
   var checked = $j(this).attr("checked");
   if(checked == true){
      enableFn();
  }else{
   disableFn();
}
});
});


