$j(document).ready(function() {
getField('weight.value').change(function() {
var birthWeight = getValue("weight.value");
if( birthWeight != null && birthWeight >= 500 && birthWeight <= 10000 ) {
var bweight = calculateBirthWeight(birthWeight);
setValue('weight.value', bweight);
}
else if(birthWeight != null && birthWeight < 10){
var bweight = birthWeight;
setValue('weight.value', bweight);
alert("BirthWeight is already in Kg and will not be converted");
}

else if ( (birthWeight != null && birthWeight > 10 && birthWeight < 500) || birthWeight > 10000 ) {
alert("please enter a valid birthweight between 500 and 10000");
}


});
});


function calculateBirthWeight(birthWeight) {
var bsa;

bsa = (birthWeight*0.001)

return bsa;
}

