												+ "-toggleContainer";
										$hfe(contentAddId).toggle(true);
										$hfe('#' + this.id).toggle(false);
										$hfe(
												'#' + parseFloat(this.id)
														+ '-removeEntry')
												.toggle(false);
										return false;
