					// This where we concatenate History of the illness to the Past Medical
					//$hfe(document)
					//		.ready(
					//				function() {
					var past = "";
					var answer = "";
					var answer1 = '';
					var answer2 = '';
					var ans1, ans2, ans3, ans4, ans5, ans6, ans7, ans8, ans9, ans10;
					ans1 = ans2 = ans3 = ans4 = ans5 = ans6 = ans7 = ans8 = ans9 = ans10 = "";

					$hfe('button.addEntry')
							.click(
									function() {
										if ($hfe("#w20 option:selected")) {
											if ($hfe("#w20 option:selected")
													.text() != "")
												ans1 = $hfe(
														"#w20 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w22 option:selected")) {
											if ($hfe("#w22 option:selected")
													.text() != "")
												ans2 = $hfe(
														"#w22 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w24 option:selected")) {
											if ($hfe("#w24 option:selected")
													.text() != "")
												ans3 = $hfe(
														"#w24 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w26 option:selected")) {
											if ($hfe("#w26 option:selected")
													.text() != "")
												ans4 = $hfe(
														"#w26 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w28 option:selected")) {
											if ($hfe("#w28 option:selected")
													.text() != "")
												ans5 = $hfe(
														"#w28 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w30 option:selected")) {
											if ($hfe("#w30 option:selected")
													.text() != "")
												ans6 = $hfe(
														"#w30 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w32 option:selected")) {
											if ($hfe("#w32 option:selected")
													.text() != "")
												ans7 = $hfe(
														"#w32 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w34 option:selected")) {
											if ($hfe("#w34 option:selected")
													.text() != "")
												ans8 = $hfe(
														"#w34 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w36 option:selected")) {
											if ($hfe("#w36 option:selected")
													.text() != "")
												ans9 = $hfe(
														"#w36 option:selected")
														.text()
														+ ", ";
										}
										if ($hfe("#w38 option:selected")) {
											if ($hfe("#w38 option:selected")
													.text() != "")
												ans10 = $hfe(
														"#w38 option:selected")
														.text()
														+ ", ";
										}

										answer = ans1 + ans2 + ans3 + ans4
												+ ans5 + ans6 + ans7 + ans8
												+ ans9 + ans10;
										$hfe(
												"span#syndromic_summary textarea")
												.val(
														past
																+ "- SYMPTOMS: "
																+ answer
																+ "\n- ILLNESS HISTORY: "
																+ answer2
																+ "\n- PAST/ANTECEDENTS: "
																+ answer1
																+ "\n- OTHER: ");
									});

					$hfe("span#past_medical textarea")
							.keyup(
									function() {
