htmlform.script:
        (function ($) {
        $(document).ready(function () {
        // Initial hiding of elements
        $('#cough1, #cough2, #cough3, #cough4, #cough5, #cough6, #classbreathid, #nosifDrug, .seroHivPosi, .vitNo, .bloodyDia, .rashed, .yesBreastFeeded, .yesOtherFoods, #diarrh1, #diarrh2, #diarrh3, #diarrh4, #diarrh5, #diarrh6, #diarrh7, #diarrh8, #diarrh9, #diarrh10, #diarrh11, #classdiarrhid, .feverc, .earc, .tb, .ulcerYes, #ulcer').hide();

        // Change events for toggling elements
        $('#harmful input').change(function () {
        $(this).val() === "1065" ? $('#nosifDrug').show() : $('#nosifDrug').hide();
        });

        $('#stoolBloody input').change(function () {
        $(this).val() === "1065" ? $('.bloodyDia').show() : $('.bloodyDia').hide();
        });

        $('#vitA input').change(function () {
        $(this).val() === "1065" ? $('.vitNo').show() : $('.vitNo').hide();
        });

        $('#sixMonthsVacc input').change(function () {
        $(this).val() === "1065" ? $('.vaccReceived').show() : $('.vaccReceived, .vaccNotReceived').hide();
        });

        $('#generalRash input').change(function () {
        $(this).val() === "1066" ? $('.rashed').show() : $('.rashed').hide();
        });

        $('#seroChild input').change(function () {
        $(this).val() === "703" ? $('.seroHivPosi').show() : $('.seroHivPosi').hide();
        });

        $('#isBreastFeed input').change(function () {
        $(this).val() === "1065" ? $('.yesBreastFeeded').show() : $('.yesBreastFeeded').hide();
        });

        $('#otherFoods input').change(function () {
        $(this).val() === "1065" ? $('.yesOtherFoods').show() : $('.yesOtherFoods').hide();
        });

        // Days-based visibility toggling
        $('#diaDays input').change(function () {
        parseInt($(this).val(), 10) >= 14 ? $('.diaHidClass').show() : $('.diaHidClass').hide();
        });

        $('#fevDays input').change(function () {
        parseInt($(this).val(), 10) >= 7 ? $('#howlong').prop('disabled', false) : $('#howlong').prop('disabled', true);
        });

        // Measles and ulcers
        $('#measleYes input').change(function () {
        if ($(this).val() === "1066") {
        $('#classMeasleId, #ulcer').show();
        } else {
        $('#classMeasleId, #ulcer').hide();
        }
        });

        $('#ulcer input').change(function () {
        $(this).val() === "1066" ? $('.ulcerYes').show() : $('.ulcerYes').hide();
        });

        // Cough toggling
        $('#coughid input').change(function () {
        if ($(this).val() === "1066") {
        $('#cough1, #cough2, #cough3, #cough4, #cough5, #cough6, #classbreathid').show();
        } else {
        $('#cough1, #cough2, #cough3, #cough4, #cough5, #cough6, #classbreathid').hide();
        }
        });

        // Diarrhoea toggling
        $('#diarrhoeaid input').change(function () {
        if ($(this).val() === "1066") {
        $('#diarrh1, #diarrh2, #diarrh3, #diarrh4, #diarrh5, #diarrh6, #diarrh7, #diarrh8, #diarrh9, #diarrh10, #diarrh11, #classdiarrhid').show();
        } else {
        $('#diarrh1, #diarrh2, #diarrh3, #diarrh4, #diarrh5, #diarrh6, #diarrh7, #diarrh8, #diarrh9, #diarrh10, #diarrh11, #classdiarrhid').hide();
        }
        });

        // Fever and ear issues toggling
        $('#feverid input').change(function () {
        $(this).val() === "1066" ? $('.feverc').show() : $('.feverc').hide();
        });

        $('#earid input').change(function () {
        $(this).val() === "1066" ? $('.earc').show() : $('.earc').hide();
        });

        // TB risk
        $('.hightb input').change(function () {
        const values = ['2133', '157626', '157595', '157625'];
        const isChecked = values.some((val) => $(`.hightb input[value="${val}"]`).is(':checked'));
        isChecked ? $('.tb').show() : $('.tb').hide();
        });

        // Anemia mappings
        $('#anemia1 input').change(function () {
        $('#anemia4 input').prop('checked', $(this).is(':checked'));
        });

        $('#anemia2 input').change(function () {
        $('#anemia5 input').prop('checked', $(this).is(':checked'));
        });

        $('#anemia3 input').change(function () {
        $('#anemia6 input').prop('checked', $(this).is(':checked'));
        });

        // Danger signs
        const dangerSelectors = ['#convulsingid', '#lethargicid', '#vomitsid', '#seizuresid'];
        dangerSelectors.forEach((selector) => {
        $(selector).click(function () {
        const isChecked = $(this).find('input[value="106329"]').is(':checked');
        const message = isChecked
        ? 'This Patient has Danger Sign;
 • Give first dose of an appropriate antibiotic.
 • Treat to prevent hypoglycemia
 • Refer URGENTLY to hospital
'
        : '';
        $('#message').html(message).css({ color: 'red', fontWeight: 'bold' });
        $('#cldenger input[value="1066"]').prop('checked', isChecked);
        });
        });

        // Page toggle
        $('.pages').click(function () {
        $(`#tb${$(this).attr('id').substring(4)}`).toggle();
        });
        });
        })(jQuery);
