        function formatDate(mydate) {
            var d = mydate.split("/");
            var month = d[1];
            var day = d[0];
            var year = d[2];

            switch (month) {
                case 'Jan':
                    month = "01";
                    break;
                case 'Feb':
                    month = "02";
                    break;
                case 'Mar':
                    month = "03";
                    break;
                case 'Apr':
                    month = "04";
                    break;
                case 'May':
                    month = "05";
                    break;
                case 'Jun':
                    month = "06";
                    break;
                case 'Jul':
                    month = "07";
                    break;
                case 'Aug':
                    month = "08";
                    break;
                case 'Sept':
                    month = "09";
                    break;
                case 'Oct':
                    month = "10";
                    break;
                case 'Nov':
                    month = "11";
                    break;
                case 'Dec':
                    month = "12";
                    break;
                default:
                    console.log(month);
            }

            return [year, month, day].join('-');
        }

        var url = new URL(window.location.href);

