                        $j('#Counselled_on_Linkage_yes').show();
                        $j('#Counselled_on_Linkage_yes_2').show();
                    } else {
                        $j('#Counselled_on_Linkage_yes').hide();
                        $j('#Counselled_on_Linkage_yes_2').hide();
                    }
                    break;
                case 'treatLink':
                    if (!getValue('treatLink.value')) {
                        $j('#noTreatLink').hide();
                        $j('#yesTreatLink').hide();
                        $j('#Counselled_on_Linkage_yes_2').hide();
                        return;
                    }
                    if (getValue('treatLink.value') == "1066") {
                        $j('#noTreatLink').show();
                        $j('#yesTreatLink').hide();
