	var getURLParam = function(name) {
	    var results = new RegExp('[\\?&]' + name + '=([^&#]*)').exec(document.URL);
	    if(results == null)
	    	return;
	    return results[1] || 0;
	};

	var isEditMode = function() {
		if(getURLParam('mode') == 'EDIT')
			return true;
		else
			return false;
     };

