$j(".checkboxGroup")
.each(
function() {
var group = $j(this);
var uncheckAll = function() {
group
.find(
"input[type$='checkbox']")
.attr("checked",
false);
group
.find(
"input[type$='checkbox']")
.change();
}
var uncheckRadioAndAll = function() {
group
.find(
"#checkboxAll,#checkboxRadio")
.find(
"input[type$='checkbox']")
.attr("checked",
false);
group
.find(
"#checkboxAll,#checkboxRadio")
.find(
"input[type$='checkbox']")
.change();
}

group
.find("#checkboxAll")
.find("input")
.click(
function() {
var flip;
var checked = $j(
this)
.siblings(
":checkbox:first")
.attr(
"checked");
if ($j(this)
.attr(
"name") == $j(
this)
.parents(
"#checkboxAll:first")
.find(
":checkbox:first")
.attr(
"name")) {
checked = $j(
this)
.attr(
"checked");
flip = checked;
} else {
flip = !checked;
}
if ($j(this)
.attr(
"type") == "text")
if (flip == false)
flip = !filp; // this is so the user doesn't go to check the checkbox, then uncheck it when they hit the input.
uncheckAll();
$j(this)
.parents(
"#checkboxAll:first")
.find(
":checkbox")
.attr(
"checked",
flip);
$j(this)
.parents(
"#checkboxAll:first")
.find(
":checkbox")
.change();
});

group
.find("#checkboxRadio")
.find(
"input[type$='checkbox']")
.click(
function() {
uncheckAll();
$j(this)
.siblings(
"input[type$='checkbox']")
.attr(
"checked",
false);
$j(this)
.attr(
"checked",
true);
$j(this)
.change();
});

group
.find(
"#checkboxCheckbox")
.click(
function() {
uncheckRadioAndAll();
});
});

});

